import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

function App() {
  const [numImages, setNumImages] = useState(0);
  const [imageData, setImageData] = useState([]);
  const [formErrors, setFormErrors] = useState([]);

  const handleNumImagesChange = (e) => {
    const num = parseInt(e.target.value) || 0;
    setNumImages(num);
    setFormErrors([]);
    setImageData(new Array(num).fill(null).map(() => ({ title: '', description: '', image: '' })));
  };

  const handleImageUpload = (e, index) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      const newImageData = [...imageData];
      newImageData[index].image = event.target.result;
      setImageData(newImageData);
    };
    reader.readAsDataURL(file);
  };

  const handleTitleChange = (e, index) => {
    const newImageData = [...imageData];
    newImageData[index].title = e.target.value;
    setImageData(newImageData);
  };

  const handleDescriptionChange = (e, index) => {
    const newImageData = [...imageData];
    newImageData[index].description = e.target.value;
    setImageData(newImageData);
  };

  const handleSubmit = () => {
    const errors = [];
    for (let i = 0; i < numImages; i++) {
      if (!imageData[i].image || !imageData[i].title || !imageData[i].description) {
        errors.push(`Image ${i + 1}: All fields are required.`);
      }
    }
    setFormErrors(errors);

    if (errors.length === 0) {
      // Submit the data (e.g., send it to the server or another component)
      console.log('Submitted Data:', imageData);
    }
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col md={6}>
          <h2 className="mb-3">Image Slideshow Form</h2>
          <Form>
            <Form.Group controlId="numImages">
              <Form.Label>Number of Images:</Form.Label>
              <Form.Control
                type="number"
                min="0"
                value={numImages}
                onChange={handleNumImagesChange}
              />
            </Form.Group>
            {Array.from({ length: numImages }, (_, index) => (
              <div key={index}>
                <Form.Group controlId={`image-${index}`}>
                  <Form.Label>Image {index + 1}</Form.Label>
                  <Form.File
                    accept="image/*"
                    onChange={(e) => handleImageUpload(e, index)}
                  />
                </Form.Group>
                <Form.Group controlId={`title-${index}`}>
                  <Form.Label>Title</Form.Label>
                  <Form.Control
                    type="text"
                    value={imageData[index].title}
                    onChange={(e) => handleTitleChange(e, index)}
                  />
                </Form.Group>
                <Form.Group controlId={`description-${index}`}>
                  <Form.Label>Description</Form.Label>
                  <Form.Control
                    type="text"
                    value={imageData[index].description}
                    onChange={(e) => handleDescriptionChange(e, index)}
                  />
                </Form.Group>
              </div>
            ))}
            <Button variant="primary" onClick={handleSubmit}>
              Submit
            </Button>
          </Form>
          {formErrors.length > 0 && (
            <div className="mt-3">
              {formErrors.map((error, index) => (
                <p key={index} className="text-danger">
                  {error}
                </p>
              ))}
            </div>
          )}
        </Col>
        <Col md={6}>
          <div className="slider-container">
            <h2 className="mb-3">Image Slideshow</h2>
            <Carousel autoPlay infiniteLoop interval={5000}>
              {imageData.map((item, index) => (
                <div key={index} className="slide">
                  <img src={item.image} alt={item.title} />
                  <h3>{item.title}</h3>
                  <p>{item.description}</p>
                </div>
              ))}
            </Carousel>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
