import React, { Component } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.css'; // Remove '/lib/styles/carousel.min.css'


class ImageForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      number: '',
      images: [],
      showForm: true,
    };
  }

  handleNumberChange = (e) => {
    this.setState({ number: e.target.value });
  };

  handleImageUpload = (e) => {
    const imageFiles = e.target.files;
    const imageArray = Array.from(imageFiles).map((file) => ({
      file,
      title: this.state.number,
      description: this.state.number,
    }));
    this.setState({ images: imageArray });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState({ showForm: false });
  };

  render() {
    return (
      <div className="app-container">
        {this.state.showForm ? (
          <div className="form-container">
            <form onSubmit={this.handleSubmit}>
              <h1>Form Title</h1>
              <p>Form Description</p>
              <div className="form-group">
                <label htmlFor="number">Number</label>
                <input
                  type="number"
                  id="number"
                  value={this.state.number}
                  onChange={this.handleNumberChange}
                />
              </div>
              <div className="form-group">
                <label htmlFor="images">Upload Images</label>
                <input
                  type="file"
                  id="images"
                  multiple
                  onChange={this.handleImageUpload}
                />
              </div>
              <button type="submit">Submit</button>
            </form>
          </div>
        ) : (
          <div className="slider-container">
            <h1>Slider Title</h1>
            <p>Slider Description</p>
            <Carousel showArrows={true} showThumbs={true}>
              {this.state.images.map((image, index) => (
                <div key={index}>
                  <img src={URL.createObjectURL(image.file)} alt={image.title} />
                  <p>{image.title}</p>
                  <p>{image.description}</p>
                </div>
              ))}
            </Carousel>
          </div>
        )}
      </div>
    );
  }
}

export default ImageForm;
