import React, { useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

function App() {
  const [formData, setFormData] = useState([]);
  const [showForm, setShowForm] = useState(true);
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Process form data and add it to formData state
    const numberInput = document.getElementById('numberInput');
    const images = formData.map((item) => ({
      title: item.title,
      description: item.description,
      imageURL: URL.createObjectURL(item.imageFile),
    }));
    formData.push({
      title: numberInput.value,
      description: numberInput.value,
      imageFile: null,
    });
    numberInput.value = '';
    setFormData([...formData]);
    setCurrentSlide(formData.length);
  };

  const handleImageChange = (e, index) => {
    const imageFile = e.target.files[0];
    formData[index].imageFile = imageFile;
    setFormData([...formData]);
  };

  const renderForm = () => {
    return (
      <form onSubmit={handleFormSubmit} style={formStyle}>
        {/* Input for number */}
        <label htmlFor="numberInput" style={labelStyle}>
          Enter a Number:
        </label>
        <input
          type="number"
          id="numberInput"
          placeholder="Enter a number"
          style={inputStyle}
        />
        {/* Image upload for each number */}
        {formData.map((item, index) => (
          <div key={index} style={imageInputStyle}>
            <label htmlFor={`imageInput-${index}`} style={labelStyle}>
              Image {index + 1}:
            </label>
            <input
              type="file"
              id={`imageInput-${index}`}
              accept="image/*"
              style={inputStyle}
              onChange={(e) => handleImageChange(e, index)}
            />
            <input
              type="text"
              placeholder="Image Title"
              style={inputStyle}
              value={item.title}
              onChange={(e) => {
                formData[index].title = e.target.value;
                setFormData([...formData]);
              }}
            />
            <textarea
              placeholder="Image Description (max 250 characters)"
              maxLength={250}
              style={textareaStyle}
              value={item.description}
              onChange={(e) => {
                formData[index].description = e.target.value;
                setFormData([...formData]);
              }}
            />
          </div>
        ))}
        <button type="submit" style={submitButtonStyle}>
          Submit
        </button>
      </form>
    );
  };

  return (
    <div style={pageStyle}>
      {showForm ? (
        renderForm()
      ) : (
        <div>
          {/* Display the image slider */}
          <Carousel
            showArrows={true}
            showStatus={false}
            showThumbs={false}
            width={400}
            selectedItem={currentSlide}
            onChange={(index) => setCurrentSlide(index)}
          >
            {formData.map((item, index) => (
              <div key={index} style={sliderItemStyle}>
                <img src={item.imageURL} alt={item.title} />
                <h2 style={sliderTitleStyle}>{item.title}</h2>
                <p style={sliderDescriptionStyle}>{item.description}</p>
              </div>
            ))}
          </Carousel>
        </div>
      )}
    </div>
  );
}

const pageStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  height: '100vh',
  background: 'linear-gradient(to right, red, green)',
};

const formStyle = {
  background: 'rgba(255, 255, 255, 0.8)',
  padding: '20px',
  borderRadius: '10px',
};

const labelStyle = {
  color: 'white',
};

const inputStyle = {
  padding: '10px',
  marginBottom: '10px',
  borderRadius: '5px',
};

const textareaStyle = {
  padding: '10px',
  marginBottom: '10px',
  borderRadius: '5px',
  minHeight: '100px',
};

const imageInputStyle = {
  margin: '20px 0',
};

const submitButtonStyle = {
  background: 'green',
  color: 'white',
  padding: '10px 20px',
  borderRadius: '5px',
  cursor: 'pointer',
};

const sliderItemStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  padding: '20px',
};

const sliderTitleStyle = {
  color: 'white',
};

const sliderDescriptionStyle = {
  color: 'white',
  textAlign: 'center',
};

export default App;
