import React, { useState } from 'react';

function FormComponent({ onSubmit }) {
  const [numImages, setNumImages] = useState(0);
  const [imageData, setImageData] = useState([]);

  const handleNumImagesChange = (e) => {
    const num = parseInt(e.target.value);
    setNumImages(num);
    setImageData([]);
    if (!isNaN(num) && num >= 0) {
        const newImageData = Array.from({ length: num }, () => ({
          title: '',
          description: '',
          file: null,
        }));
        setImageData(newImageData);
        setNumImages(num);
      }
  };

  const handleImageUpload = (e, index) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onload = (event) => {
      const newImageData = [...imageData];
      newImageData[index] = {
        image: event.target.result,
        title: '',
        description: '',
      };
      setImageData(newImageData);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleTitleChange = (e, index) => {
    const newImageData = [...imageData];
    newImageData[index].title = e.target.value;
    setImageData(newImageData);
  };

  const handleDescriptionChange = (e, index) => {
    const newImageData = [...imageData];
    newImageData[index].description = e.target.value;
    setImageData(newImageData);
  };

  const handleSubmit = () => {
    onSubmit(imageData);
  };

  return (
    <div className="form-container">
        <div class="css-1dbjc4n r-kemksi r-16y2uox r-1wbh5a2"><div class="css-1dbjc4n r-1pz39u2 r-16y2uox r-1wbh5a2"><div tabindex="0" class="css-1dbjc4n"></div><div role="group" tabindex="0" class="css-1dbjc4n r-kemksi r-6koalj r-16y2uox r-1wbh5a2"><div class="css-1dbjc4n"><div class="css-1dbjc4n r-1h3ijdo r-136ojw6"><div class="css-1dbjc4n"><div class="css-1dbjc4n r-1e5uvyk r-5zmot r-1h3ijdo"><div class="css-1dbjc4n r-1awozwy r-18u37iz r-1h3ijdo r-1777fci r-1jgb5lz r-1ye8kvj r-ymttw5 r-13qz1uu"><div class="css-1dbjc4n r-1habvwh r-1pz39u2 r-1t2qqvi r-16y2uox r-1wbh5a2 r-1777fci r-15ysp7h r-s8bhmr"><div aria-label="إغلاق" role="button" tabindex="0" class="css-18t94o4 css-1dbjc4n r-1niwhzg r-sdzlij r-1phboty r-rs99b7 r-2yi16 r-1qi8awa r-1ny4l3l r-o7ynqc r-6416eg r-lrvibr" data-testid="app-bar-close" style="border-color: rgba(0, 0, 0, 0); margin-right: calc(-8px);"><div dir="rtl" class="css-901oao r-1awozwy r-6koalj r-18u37iz r-16y2uox r-1k78y06 r-a023e6 r-b88u0q r-1777fci r-rjixqe r-bcqeeo r-q4m81j r-qvutc0" style="color: rgb(239, 243, 244);"><svg viewBox="0 0 24 24" aria-hidden="true" class="r-4qtqp9 r-yyyyoo r-z80fyv r-dnmrzs r-bnwqim r-1plcrui r-lrvibr r-19wmn03" style="color: rgb(239, 243, 244);"><g><path d="M10.59 12L4.54 5.96l1.42-1.42L12 10.59l6.04-6.05 1.42 1.42L13.41 12l6.05 6.04-1.42 1.42L12 13.41l-6.04 6.05-1.42-1.42L10.59 12z"></path></g></svg><span class="css-901oao css-16my406 css-1hf3ou5 r-1k78y06 r-a023e6 r-rjixqe r-bcqeeo r-qvutc0" style="border-bottom: 2px solid rgb(239, 243, 244);"></span></div></div></div><div class="css-1dbjc4n r-1awozwy r-16y2uox r-1wbh5a2 r-1pi2tsx r-1777fci"><div class="css-1dbjc4n r-1habvwh"></div><div class="css-1dbjc4n r-1awozwy r-1pz39u2 r-6koalj r-16y2uox r-1777fci r-4wgw6l"><svg viewBox="0 0 24 24" aria-label="X" role="img" class="r-1nao33i r-4qtqp9 r-yyyyoo r-16y2uox r-lwhw9o r-dnmrzs r-bnwqim r-1plcrui r-lrvibr"><g><path d="M18.244 2.25h3.308l-7.227 8.26 8.502 11.24H16.17l-5.214-6.817L4.99 21.75H1.68l7.73-8.835L1.254 2