  return (
    <div>
      <h1>Image Slideshow Form</h1>
      <label htmlFor="numImages">Number of Images:</label>
      <input
        type="number"
        id="numImages"
        value={numImages}
        onChange={handleNumImagesChange}
      />
      {Array.from({ length: numImages }, (_, index) => (
        <div key={index}>
          <input
            type="file"
            accept="image/*"
            onChange={(e) => handleImageUpload(e, index)}
          />
          <input
            type="text"
            placeholder="Title"
            value={titleData[index] || ''}
            onChange={(e) => setTitleData(e.target.value, index)}
          />
          <input
            type="text"
            placeholder="Description"
            value={descData[index] || ''}
            onChange={(e) => setDescData(e.target.value, index)}
          />
        </div>
      ))}
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
}

export default FormComponent;
