import React, { useState } from 'react';
import './App.css';

// Form Component
function Form({ onSubmit }) {
  const [number, setNumber] = useState('');
  const [showImages, setShowImages] = useState(false);

  const handleNumberChange = (e) => {
    const newNumber = e.target.value;
    setNumber(newNumber);
    setShowImages(false); // Reset to hide images when the number changes
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(number);
    setShowImages(true);
  }

  return (
    <div className="form-container">
      <h2 style={{ color: 'green' }}>Form</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="number">Enter a number: </label>
        <input type="number" id="number" value={number} onChange={handleNumberChange} />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

// Slider Component
function Slider({ images }) {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleDotClick = (index) => {
    setCurrentIndex(index);
  }

  const prevSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === 0 ? images.length - 1 : prevIndex - 1));
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex === images.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className="slider-container">
      <div className="slider">
        {images.map((image, index) => (
          <div
            key={index}
            className={`slider-item ${index === currentIndex ? 'active' : ''}`}
          >
            <img src={image.url} alt={image.title} />
            <div className="caption" style={{ color: 'red' }}>
              <h3>{image.title}</h3>
              <p>{image.description}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="slider-dots">
        {images.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => handleDotClick(index)}
          ></span>
        ))}
      </div>
      <button className="prev-button" onClick={prevSlide}>
        &#10094;
      </button>
      <button className="next-button" onClick={nextSlide}>
        &#10095;
      </button>
    </div>
  );
}

function App() {
  const [images, setImages] = useState([]);
  const [showSlider, setShowSlider] = useState(false);

  const handleSubmit = (number) => {
    // Simulate fetching images based on the number entered
    const newImages = [];
    for (let i = 1; i <= number; i++) {
      newImages.push({
        url: `https://via.placeholder.com/400x400?text=Image${i}`,
        title: `Image ${i}`,
        description: `Description for Image ${i}`,
      });
    }
    setImages(newImages);
    setShowSlider(true);
  };

  return (
    <div className="app">
      {showSlider ? (
        <Slider images={images} />
      ) : (
        <Form onSubmit={handleSubmit} />
      )}
    </div>
  );
}

export default App;
