import React, { useState } from 'react';
import './App.css';
import Form from './Form'; // Import the updated Form component
import Slider from './Slider'; // Assuming you have a Slider component from the previous example
// import Slider from './Slider'; // Adjust the import path if necessary

const AppContainer = styled.div`
  background-color: #f0f0f0;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

function App() {
  const [images, setImages] = useState([]);
  const [showSlider, setShowSlider] = useState(false);

  const handleSubmit = (number) => {
    setShowSlider(true);
  };

  return (
    <AppContainer>
      {showSlider ? (
        <Slider images={images} />
      ) : (
        <Form onSubmit={handleSubmit} />
      )}
    </AppContainer>
  );
}

export default App;
