import React, { useState } from 'react';
import './Form.css'; // Import your CSS file
import Slideshow from './Slideshow'; // Import your Slideshow component

function Form() {
  const [description, setDescription] = useState('');
  const [size, setSize] = useState('');
  const [number, setNumber] = useState(0);
  const [images, setImages] = useState([]);

  const handleNumberChange = (e) => {
    const newNumber = parseInt(e.target.value, 10);
    setNumber(newNumber);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Create a slideshow with the selected images
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="text" placeholder="Size" value={size} onChange={(e) => setSize(e.target.value)} />
        <input type="number" placeholder="Number" value={number} onChange={handleNumberChange} />

        <div className="image-inputs">
          {Array.from({ length: number }).map((_, index) => (
            <input key={index} type="file" accept="image/*" />
          ))}
        </div>

        <button type="submit">Submit</button>
      </form>

      {images.length > 0 && <Slideshow images={images} />}
      {/* <Slideshow images={images} /> */}

    </div>
  );
}

export default Form;
