import React, { useState } from "react";

const SlideForm = ({ onSubmit }) => {
  const [slideCount, setSlideCount] = useState(1);
  const [slides, setSlides] = useState([{ title: "", description: "", image: "" }]);

  const handleSlideCountChange = (e) => {
    const count = parseInt(e.target.value);
    setSlideCount(count);
    setSlides(new Array(count).fill({ title: "", description: "", image: "" }));
  };

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const updatedSlides = [...slides];
    updatedSlides[index][name] = value;
    setSlides(updatedSlides);
  };

  const handleImageUpload = (e, index) => {
    const file = e.target.files[0];
    const updatedSlides = [...slides];
    updatedSlides[index].image = URL.createObjectURL(file);
    setSlides(updatedSlides);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(slides);
  };

  return (
    <div>
      <h2>Create Slideshow</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="slideCount">Number of Slides:</label>
        <input
          type="number"
          id="slideCount"
          name="slideCount"
          value={slideCount}
          onChange={handleSlideCountChange}
        />
        {slides.map((slide, index) => (
          <div key={index}>
            <h3>Slide {index + 1}</h3>
            <div>
              <label htmlFor={`title${index}`}>Title:</label>
              <input
                type="text"
                id={`title${index}`}
                name="title"
                value={slide.title}
                onChange={(e) => handleInputChange(e, index)}
              />
            </div>
            <div>
              <label htmlFor={`description${index}`}>Description:</label>
              <input
                type="text"
                id={`description${index}`}
                name="description"
                value={slide.description}
                onChange={(e) => handleInputChange(e, index)}
              />
            </div>
            <div>
              <label htmlFor={`image${index}`}>Image:</label>
              <input
                type="file"
                id={`image${index}`}
                accept="image/*"
                onChange={(e) => handleImageUpload(e, index)}
              />
              {slide.image && <img src={slide.image} alt={`Slide ${index + 1}`} width="100" />}
            </div>
          </div>
        ))}
        <button type="submit">Create Slideshow</button>
      </form>
    </div>
  );
};

export default SlideForm;
