import React, { useState } from 'react';
import './App.css';
import FormComponent from './FormComponent';
import SliderComponent from './SliderComponent';

function App() {
  const [imageData, setImageData] = useState([]);

  const handleImageDataSubmit = (data) => {
    setImageData(data);
  };

  const [lightboxIsOpen, setLightboxIsOpen] = useState(false);
  const [lightboxData, setLightboxData] = useState({
    title: '',
    description: '',
    imageUrl: '',
  });

  // Function to open the lightbox
  const openLightbox = (title, description, imageUrl) => {
    setLightboxData({ title, description, imageUrl });
    setLightboxIsOpen(true);
  };

  // Function to close the lightbox
  const closeLightbox = () => {
    setLightboxIsOpen(false);
  };

  return (
    <Container fluid className="App"> {/* Use Container from React Bootstrap */}
      <Row className="content-container">
        <Col md={6}>
          {/* ... (Previous code) */}
        </Col>
        <Col md={6} className="slider-container">
          <SliderComponent imageData={imageData} openLightbox={openLightbox} />
        </Col>
      </Row>
      <Lightbox
        isOpen={lightboxIsOpen}
        onRequestClose={closeLightbox}
        {...lightboxData}
      />
    </Container>
  );
}


export default App;
